// ==UserScript==
// @id             27c0b57b-1435-4e12-85b0-8c28cee84851
// @name           ebay-watch-list
// @namespace      wtrx005
// @author         wtrx005 and moopet
// @copyright      2011+, wtrx005 (https://userscripts-mirror.org/users/370514) | moopet (https://userscripts-mirror.org/users/34917)
// @description    This script views a button in the search result on ebay to add the item directly to your watch list. based on ebay-watch-list from moopet i make this workaround for tld .at and .de! again special thanks to moopet!
// @version        2019.01.12
// @website        https://userscripts-mirror.org/scripts/show/110841
// @include        https://*.ebay.com/*
// @include        https://*.ebay.co.uk/*
// @include        https://*.ebay.de/*
// @include        https://*.ebay.at/*
// @exclude        https://my.ebay.*
// @grant          GM_xmlhttpRequest
// @noframes
// ==/UserScript==

// base init
var numCells = 0;
var IseBayStore = false;
var cells = null;

// allow automatic change the tld
var tld = window.location.hostname.split('ebay')[1];

var myEbayURL = 'https://www.ebay' + tld + '/myb/WatchList';
var myAddURL = 'https://cgi1.ebay' + tld + '/ws/eBayISAPI.dll?MakeTrack&item=';
var myRemoveURL = 'https://my.ebay' + tld + '/ws/eBayISAPI.dll?MyEbayBeta&vi=true&tagId=-99&SubmitAction.DeleteListEntries=x&entityId=';
myEbayURL = 'https://my.ebay' + tld + '/ws/eBayISAPI.dll?MyEbayBeta&CurrentPage=MyeBayNextWatching&SubmitAction.ItemsPerPageSave=x&CustomizedContainer=240&ItemsPerPage=200&View=MYEBAY_NEXT_CONTAINER_CUSTOMIZE&NewPeriod=Last31Days&ssPageName=STRK:MEWAX:COUNT:200&tagname=&View=WatchingNext#GoTo240';
var placeholderImg="data:image/png;base64,";
var unwatchedImg="data:image/png;base64,";
var watchedImg="data:image/png;base64,";
var unwatchedTitle = 'add this item to your watch list';
var watchedTitle = 'this item is being watched in My Ebay';

// using img.src to load transactions is a quick hack, but it works, quickly, and from outside GM.
var requestImg = document.createElement('img');
requestImg.name = 'requestImg';
document.body.insertBefore(requestImg, document.body.firstChild);

// check if it is a ebay store
if (window.location.hostname.split('.ebay')[0] == 'stores') {
	cells = document.getElementsByClassName('image');
} else {
	// loop through all the items listed on the page
	var lResultSet = document.getElementById('ResultSetItems');
    if (lResultSet == undefined) {
        lResultSet = document.getElementById('srp-river-results');
    }

	// only install if we've found a ResultSet
	if (lResultSet != null){
		cells = lResultSet.getElementsByClassName('sresult');
        if (cells.length == 0) {
            cells = lResultSet.getElementsByClassName('s-item');
        }
	}
}

// if cells found get the length
if (cells != null){
	numCells = cells.length;
}

// only install if the user is logged in and we've found the elements to change
var tmp = document.getElementById('gh-ug');
if (tmp != null && tmp.innerHTML.indexOf('SignIn') < 0 && numCells > 0)
{
	// put placeholder "loading..." elements in while the script does its thing
	var newHTML = '<br /><input disabled="disabled" value="wait..." type="image" src="' + placeholderImg + '" style="position: absolute; top:0; left:0;" />';
	for (var c = 0; c < numCells; c++)
	{
        cells[c].innerHTML += newHTML;
	}

	// request the "my ebay" page
	GM_xmlhttpRequest(
	{
		method: 'GET',
		url: myEbayURL,
/*        headers:
		{
			'User-agent': 'Mozilla/4.0 (compatible) Greasemonkey',
			'Accept': 'application/atom+xml,application/xml,text/xml',
		},*/
		onload: function(responseDetails)
		{
			var myEbayContents = responseDetails.responseText;
			for (var i = 0; i < numCells; i++)
			{
				var anchor = cells[i].getElementsByTagName('a')[0];

				if (anchor != null){
					var id = anchor.href.match(/\/(\d+)(\?)/);

					if (id != null && id.length > 1)
					{
						// take just the numerical part, which is the ID.
						id = id[1];

						// see if that ID exists on the My Ebay page - which means it's being watched already
						var onWatchPage = (myEbayContents.match(id) == null) ? 0 : 1;

						var itemID = 'xyz_item_' + i;
						var statusID = 'xyz_status_' + i;
						var imageID = 'xyz_image_' + i;
						var anchorID = 'xyz_anchor_' + i;
						var title = onWatchPage ? watchedTitle : unwatchedTitle;

						newHTML  = '<a href="javascript:void(0)" id="' + anchorID + '" style="position: absolute; top:0; left:0;"><img id="' + imageID + '" name="' + imageID + '" src="' + (onWatchPage ? watchedImg : unwatchedImg) + '" title="' + title + '" /><input id="' + statusID + '" name="' + statusID + '" type="hidden" value="' + onWatchPage + '" /><input id="' + itemID + '" name="' + itemID + '" type="hidden" value="' + id + '" /></a>';

						// hide "loading..." element
						tmp = cells[i].getElementsByTagName('input');
						if (tmp)
						{
							tmp[0].style.display = 'none';
						}

						// add proper button
						cells[i].innerHTML += newHTML;
						var el = document.getElementById(anchorID);

						// add event handler
						el.addEventListener("click", function(e)
						{
							var i, anchor, item, status, button;

							anchor = e.target;
							i = anchor.id.split('_')[2];
							item = document.getElementById('xyz_item_' + i);
							status = document.getElementById('xyz_status_' + i);
							button = document.getElementById('xyz_image_' + i);

							if (status.value == '0')
							{
								var requestURL = myAddURL + '' + item.value;
								requestImg.src = requestURL;
								button.src = watchedImg;
								status.value = 1;
								anchor.title = watchedTitle;
							}
							else
							{
								requestURL = myRemoveURL + '' + item.value;
								requestImg.src = requestURL;
								button.src = unwatchedImg;
								status.value = 0;
								anchor.title = unwatchedTitle;
							}
						}, false);
					}
				}
			}
		}
	});
}

